from elasticsearch import Elasticsearch, helpersimport jsones = Elasticsearch(    "https://localhost:9200",    basic_auth=("elastic", "A5p5tbLTQHRHPd9Psnk4"),    verify_certs=False,    ssl_show_warn=False)if es.indices.exists(index="books"):    es.indices.delete(index="books")es.indices.create(index="books")with open("books.json", "r") as file:    books = json.load(file)actions = [    {        "_index": "books",        "_id": i + 1,        "_source": {            "Title": book["Title"],            "Authors": book["Authors"],            "Description": book["Description"],            "Category": book["Category"],            "Publisher": book["Publisher"],            "Price_Starting_With_($)": float(book["Price_Starting_With_($)"]),            "Publish_Date_(Month)": book["Publish_Date_(Month)"],            "Publish_Date_(Year)": book["Publish_Date_(Year)"],        },    }    for i, book in enumerate(books)]helpers.bulk(es, actions)print("Books indexed successfully!")